mapper.select:
SELECT COUNT(*)
FROM trip_plan tp
WHERE
	tp.plan_no = #{planNo}
	AND
	tp.member_nickname = #{loginNickname}

SELECT * FROM ( SELECT ROWNUM AS rnum, inner.* FROM ( SELECT p.place_id, p.place_thumb, p.place_title, p.place_type_id, p.place_addr, p.map_lat, p.map_lng, NVL(r.avg_rate, 0) AS place_rating, NVL(r.review_count, 0) AS place_review FROM place_info p LEFT JOIN ( SELECT place_id, AVG(star_rate) AS avg_rate, COUNT(*) AS review_count FROM review GROUP BY place_id ) r ON p.place_id = r.place_id WHERE p.map_lat BETWEEN #{lat} - #{height} / 2.0 AND #{lat} + #{height} / 2.0 AND p.map_lng BETWEEN #{lng} - #{width} / 2.0 AND #{lng} + #{width} / 2.0
AND p.place_type_id = #{filterOption}
AND p.place_type_id NOT IN (32, 39, 12)
ORDER BY 6371000 * ACOS( COS(#{lat} * 3.141592 / 180) * COS(p.map_lat * 3.141592 / 180) * COS((p.map_lng - #{lng}) * 3.141592 / 180) + SIN(#{lat} * 3.141592 / 180) * SIN(p.map_lat * 3.141592 / 180) )
ORDER BY place_review DESC
ORDER BY p.place_title ASC
) inner WHERE ROWNUM <= #{start} + #{size} ) WHERE rnum > #{start}

SELECT * FROM ( SELECT ROWNUM AS rn, inner_query.* FROM ( SELECT tp.*, NVL(pb.bookmark_count, 0) AS bookmark_count, CASE WHEN pb2.member_nickname IS NOT NULL THEN 1 ELSE 0 END AS bookmarked FROM trip_plan tp
LEFT JOIN ( SELECT plan_no, COUNT(*) AS bookmark_count FROM plan_bookmark GROUP BY plan_no ) pb ON tp.plan_no = pb.plan_no
LEFT JOIN ( SELECT plan_no, member_nickname FROM plan_bookmark WHERE member_nickname = #{loginNickname} ) pb2 ON tp.plan_no = pb2.plan_no
tp.member_nickname = #{memberNickname}
AND tp.plan_no IN
#{item}
ORDER BY
tp.plan_no DESC
bookmark_count DESC
tp.plan_no DESC
) inner_query WHERE ROWNUM < #{end} ) WHERE rn >= #{start}
