mapper.delete:
delete from likes
where review_no = #{reviewNo}
and member_nickname = #{memberNickname}

mapper.update:
update comm set
comm_content=#{commContent}
where comm_no =#{commNo}

update review set
review_title = #{reviewTitle},
star_rate = #{starRate},
review_content = #{reviewContent}
WHERE review_no = #{reviewNo}
